import m from "mithril"

export const Button = {

    oncreate(vnode) {

        componentHandler.upgradeElement(vnode.dom)
    },

    view(vnode) {

        const {
            className = "",
            type = "button",
            disabled = false,
         } = vnode.attrs

        return (
            <button
                disabled={disabled}
                className={`mdl-button mdl-js-button ${className}`}>
                {vnode.children}
            </button>
        )
    }
}