import m from "mithril"
import classNames from "classnames"

export const Icon = {

    oncreate(vnode) {

        componentHandler.upgradeElement(vnode.dom)
    },

    view(vnode) {

        const {
            className = "",
            type = "",
         } = vnode.attrs
        const classes = classNames(
            "mdl-button",
            "mdl-js-button",
            "mdl-button--icon",
            className,
        )

        return (
            <button className={classes}>
                <i class="material-icons">{type}</i>
            </button>
        )
    }
}