import m from "mithril"

import classNames from "classnames"

export const Spinner = {

    oncreate(vnode) {

        componentHandler.upgradeElement(vnode.dom)
    },

    view(vnode) {

        const {
            active,
        } = vnode.attrs

        const classes = classNames(
            vnode.attrs.className,
            "mdl-spinner",
            "mdl-js-spinner",
            "is-upgraded",
            { "is-active": active },
        )
        return (
            <div className={classes}></div>
        )
    }
}